    AND `updator_id` = #{updatorId}


    AND DATE_FORMAT(update_time,'%Y-%m-%d') = #{updateTime},


    AND DATE_FORMAT(record_time,'%Y-%m-%d') = #{recordTime}


    AND flag = #{flag}

