mapper.delete:
DELETE FROM t_res


    AND record_id = #{recordId}


    AND type_id = #{typeId}


    AND res_name = #{resName}


    AND ass_id = #{assId}


    AND ass_type_id = #{assTypeId}


    AND editor_id = #{editorId}


    AND auditor_id = #{auditorId}


    AND record_flag = #{recordFlag}


    AND audit_status = #{auditStatus}


    AND announcer_id = #{announcerId}


    AND org_id = #{orgId}


    AND atta_content = #{attaContent}

DELETE FROM t_res_dl


    AND record_id = #{recordId}


    AND res_id = #{resId}


    AND user_id = #{userId}


    AND record_flag = #{recordFlag}

mapper.select:
SELECT
  record_id, res_name, type_id, ass_id, ass_type_id, original_name, DATE_FORMAT(publish_time,'%Y-%m-%d') publish_time,
  DATE_FORMAT(record_time,'%Y-%m-%d %H:%i:%s') record_time, record_flag,
  res_size, announcer_id, res_author, res_src, res_intro, res_desc, res_tags,
  DATE_FORMAT(modify_time,'%Y-%m-%d %H:%i:%s') modify_time,
  editor_id, access_url, auditor_id, DATE_FORMAT(audit_time,'%Y-%m-%d %H:%i:%s') audit_time,
  audit_status, type_name, '' AS ass_title, ass_type_name, announcer_name, editor_name, auditor_name,
  curr_name, org_id, atta_content
FROM (
  SELECT
    t1.*,
    t2.user_name announcer_name,
    t3.user_name editor_name,
    t4.user_name auditor_name,
    t5.`name` type_name,
    t6.`name` ass_type_name
  FROM
    (SELECT * FROM t_res ORDER BY publish_time, record_time DESC) t1
    LEFT JOIN t_user t2 ON t1.announcer_id = t2.user_id
    LEFT JOIN t_user t3 ON t1.editor_id = t3.user_id
    LEFT JOIN t_user t4 ON t1.auditor_id = t4.user_id
    LEFT JOIN t_category t5 ON t5.record_id = t1.type_id
    LEFT JOIN t_category t6 ON t6.record_id = t1.ass_type_id
) t


    AND record_id = #{recordId}


    AND type_id = #{typeId}


    AND res_name = #{resName}


    AND ass_id = #{assId}


    AND ass_type_id = #{assTypeId}


    AND editor_id = #{editorId}


    AND auditor_id = #{auditorId}


    AND record_flag = #{recordFlag}


    AND audit_status = #{auditStatus}


    AND announcer_id = #{announcerId}


    AND org_id = #{orgId}


    AND (UPPER(res_name) LIKE CONCAT('%',UPPER(#{key}),'%')
    )


    AND announcer_id IN

      #{itemId}



    AND org_id IN

      #{itemId}



    AND DATE_FORMAT(publish_time, '%Y-%m') = #{yearMonth}


ORDER BY t.publish_time DESC, t.record_time DESC

SELECT record_id, res_id, user_id, DATE_FORMAT(dl_time,'%Y-%m-%d %H:%i:%s') dl_time, record_flag, res_name, res_original_name, user_name, type_id, type_name, ass_id, ass_type_id, ass_title, ass_type_name FROM ( SELECT t10.*, t11.user_name, t12.res_name, t12.res_original_name, t12.type_id, t12.type_name, t12.ass_id, t12.ass_type_id, t12.ass_title, t12.ass_type_name FROM (SELECT * FROM t_res_dl ORDER BY dl_time DESC) t10 LEFT JOIN t_user t11 ON t10.user_id = t11.user_id LEFT JOIN ( SELECT t1.*, '' AS ass_title, t5.`name` type_name, t6.`name` ass_type_name FROM ( SELECT record_id, res_name, original_name res_original_name, type_id, ass_id, ass_type_id FROM t_res WHERE record_flag = 1 ) t1 LEFT JOIN t_category t5 ON t5.record_id = t1.type_id LEFT JOIN t_category t6 ON t6.record_id = t1.ass_type_id ) t12 ON t10.res_id = t12.record_id )t
AND record_id = #{recordId}
AND res_id = #{resId}
AND user_id = #{userId}
AND record_flag = #{recordFlag}
AND (UPPER(res_name) LIKE CONCAT('%',UPPER(#{key}),'%') OR UPPER(res_original_name) LIKE CONCAT('%',UPPER(#{key}),'%') OR UPPER(user_name) LIKE CONCAT('%',UPPER(#{key}),'%') OR UPPER(type_name) LIKE CONCAT('%',UPPER(#{key}),'%') OR UPPER(ass_type_name) LIKE CONCAT('%',UPPER(#{key}),'%') )
ORDER BY t.dl_time DESC
